function draw_fisherman () {
    if (fisherman_seq == 0) {
        sprite_fisherman.destroy()
        sprite_fisherman = sprites.create(img_fisherman, SpriteKind.Player)
        sprite_fisherman.setPosition(fisherman_x, fisherman_y)
    } else {
        sprite_fisherman.destroy()
        sprite_fisherman = sprites.create(img_fisherman_a, SpriteKind.Player)
        sprite_fisherman.setPosition(fisherman_x, fisherman_y)
    }
}
function create_poleline () {
    pole_no_line = false
    sprite_hook.destroy()
    sprite_hook = sprites.create(img_hook.clone(), SpriteKind.Player)
    pole_line_move(0, true)
    draw_line_pole2water()
}
function animate_fisherman () {
    fisherman_seq = randint(0, 1)
}
function pole_line_move (increment: number, change_minmax: boolean) {
    pole_top_x = pole_top_x + increment
    if (pole_top_x <= pole_tip_x) {
        pole_top_x = pole_tip_x
    }
    if (change_minmax) {
        pole_top_min_x = pole_top_x - 1
        pole_top_max_x = pole_top_x + 1
    }
}
info.onCountdownEnd(function () {
    game.splash("Game Over!" + "\n" + "Score: " + info.score().toString())
    game.reset()
})
function draw_fish () {
    // Circle(cen) = (x – h)2 + (y – k)2 = r2
    // x = h + r*cos@
    // y = k + r*sin@
    // rad = @*PI/180
    fish_xy_rad = fish_xy_angle * 3.14 / 180
    if (fish_sees_hook) {
        fish_x = sprite_hook.x
        fish_y = sprite_hook.y
        sprite_fish.setPosition(fish_x + sprite_fish.width / 2, fish_y)
        if (fish_on_hook == 0) {
            fish_on_hook = 1
            if (fish_dir == 0) {
                sprite_fish.image.flipX()
            }
            sprite_hook.image.flipX()
        }
        if (pole_no_line && fish_on_hook) {
            sprite_hook.image.flipX()
            sprite_fish.image.flipX()
            sprite_fish.setFlag(SpriteFlag.Invisible, true)
        }
    } else {
        sprite_fish.setPosition(fish_x + 5 * Math.cos(fish_xy_rad), fish_y + 5 * Math.sin(fish_xy_rad))
    }
}
