function draw_line_pole2water () {
    if (pole_no_line) {
        sprite_line.setFlag(SpriteFlag.Invisible, true)
        sprite_hook.setFlag(SpriteFlag.Invisible, true)
        sprite_water_ripple.setPosition(pole_top_x, pole_top_y)
        sprite_water_ripple.setFlag(SpriteFlag.Invisible, false)
    } else {
        sprite_line.destroy()
        sprite_line = sprites.create(img_clear.clone(), SpriteKind.Player)
        sprite_line.image.drawLine(pole_tip_x, pole_tip_y, pole_top_x, pole_top_y, 15)
        sprite_line.image.drawLine(pole_top_x, pole_top_y + 8, pole_top_x, pole_top_y + pole_inwater_length + 8, 15)
        sprite_water_ripple.setPosition(pole_top_x, pole_top_y)
        sprite_hook.setPosition(pole_top_x, pole_top_y + pole_inwater_length + 8 + 3)
        sprite_hook.setFlag(SpriteFlag.Invisible, false)
    }
}
function animate_fish () {
    if (fish_xy_angle >= 359) {
        fish_xy_angle = 0
    } else {
        fish_xy_angle += randint(-15, 15)
    }
    if (fish_dir && sprite_hook.x <= sprite_fish.x || !(fish_dir) && sprite_hook.x >= sprite_fish.x) {
        fish_to_hook = Math.ceil(Math.sqrt((sprite_hook.x - sprite_fish.x) ** 2 + (sprite_hook.y - sprite_fish.y) ** 2))
        fish_sees_hook = (!fish_sees_hook)?((fish_to_hook<((sprite_fish.width/2)+5))?true:false):true
    } else {
        fish_to_hook = 0
    }
}
function create_fish () {
    sprite_fish.destroy()
    sprite_fish = sprites.create(img_fish_a.clone(), SpriteKind.Enemy)
    fish_xy_angle = 0
    fish_x = randint(30, scene.screenWidth() - sprite_fish.width)
    fish_y = randint(80 + sprite_fish.height, 120 - sprite_fish.height)
    fish_sees_hook = false
    fish_on_hook = 0
    fish_dir = randint(0, 1)
    sprite_fish.setPosition(fish_x, fish_y)
    sprite_fish.setFlag(SpriteFlag.Invisible, false)
    if (fish_dir == 1) {
        sprite_fish.image.flipX()
    }
    draw_fish()
}
function animate_line_pole2water () {
    if (!(pole_no_line)) {
        // Wave movement
        if (pole_top_x <= pole_top_min_x || pole_top_x >= pole_top_max_x) {
            pole_top_dir = !(pole_top_dir)
        }
    } else {
        // Casting
        if (pole_top_x <= pole_tip_x || pole_top_x >= 150) {
            pole_top_dir = !(pole_top_dir)
        }
    }
    pole_line_move(pole_top_dir?1:-1, false)
}
