init_sprites()
let pole_fishman2tip_x = pole_tip_x - fisherman_x
let pole_fishman2tip_y = pole_tip_y - fisherman_y
create_fish()
if (!(debug)) {
    game.splash("FISH")
    game.splash("Left/Right: Move Fisherman", "A: Reel B: Cast/Release")
    info.startCountdown(15)
}
info.setScore(0)
forever(function () {
    if (pole_no_line) {
        animate_line_pole2water()
    }
})
forever(function () {
    // On Fishman move
    if (controller.left.isPressed()) {
        fisherman_x += 0 - 1
        pole_tip_x = fisherman_x + pole_fishman2tip_x
    } else if (controller.right.isPressed()) {
        fisherman_x += 1
        pole_tip_x = fisherman_x + pole_fishman2tip_x
    } else {
        sprite_fisherman.vx = 0
    }
    // Correct Fishman over move
    if (fisherman_x >= 32) {
        fisherman_x = 32
        pole_tip_x = fisherman_x + pole_fishman2tip_x
    } else if (fisherman_x <= 8) {
        fisherman_x = 8
        pole_tip_x = fisherman_x + pole_fishman2tip_x
    }
    // On Reel line
    if (controller.A.isPressed()) {
        if (!(pole_no_line)) {
            if (pole_top_x == pole_tip_x) {
                if (pole_inwater_length > 0) {
                    pole_inwater_length += 0 - 1
                } else {
                    pole_no_line = true
                    if (fish_on_hook) {
                        info.changeScoreBy(1)
                        if (!(debug)) {
                            info.startCountdown(15)
                        }
                        create_fish()
                    }
                }
            } else {
                pole_line_move(-1, true)
            }
        }
    }
    // Cast/Release Line
    if (controller.B.isPressed()) {
        if (pole_no_line) {
            create_poleline()
        } else {
            if (pole_inwater_length <= 30) {
                pole_inwater_length += 1
            }
        }
    }
})
